<?php/** * Created by PhpStorm. * User: mytoken * Date: 2019-05-15 * Time: 11:59 */namespace App\Resources;use App\Ship\Http\Json\ApiResource;class AdminUserInfoResource extends ApiResource{    public $resources;    public function toArray($request)    {        return [            'id'       => $this->resource->id,            'user_name'   => $this->resource->name,            'avatar' => $this->resource->avatar,            'roles'  => json_decode($this->resource->roles, false),        ];    }}