<?php/** * Created by PhpStorm. * User: mytoken * Date: 2019-05-16 * Time: 17:15 */namespace App\Resources\Product;use App\Ship\Http\Json\ApiResource;class ProductListResource extends ApiResource{    public $resources;    public function toArray($request)    {        return [            'id'       => $this->resource->id,            'category_id'   => $this->resource->category_id,            'name' => $this->resource->name,            'sold_total' => $this->resource->sold,            'is_recommend' => $this->resource->is_recommend,            'category_name' => $this->resource->category->name        ];    }}